        left: 0;
        font-size:18px;
    }

    &__txt::before {
        content: "";
        display: block;
        width: 30px;
        height: 1px;
        margin: 0;
        background-color: #fff;
        margin-top: 10px;
        margin-bottom: 10px;

    }

}


/*----- section--visual -----*/
.section--visual {
    padding-bottom:0;
    &__container {
        @include container_w;
        @include bg_img_big;
        height: calc(100vh - #{$nav_h});        
        background-image: url('#{$base_url}/vol/slide_pc.png');
    }

    &__logo-wrapper {
        width: calc(100% - 30px);
        max-width: 320px;
        height:100%;
        position: relative;
        margin:0 auto;
    }

    &__logo {
        @include indent;
        width:100%;
        height:0;
        margin:0;
        padding-top:68%;
        position:absolute;
        top:22%;
        left:0;
        background-image: url('#{$base_url}/design/logo.svg');
        background-repeat: no-repeat;
        
    }
    
    &__webzine-title {
        position:absolute;
        bottom:-14px;
        text-indent:0;
        font: {
            weight: normal;
            size: 20px;
        }        
        color:#fff;
    }
    &__scroll-bottom {
        position: absolute;
        bottom: 40px;
        left: 50%;
        transform: translateX(-50%);
    }
}
.scroll-bottom {
    text-align:center;   
	color: #fff;
