/*section--subscribe*/
.section--subscribe {
    padding: 100px 0;
    background-color: $color_hover;

    &__container {
        @include container_w(1170px);
        padding: 0 15px;
        overflow:hidden;
    }

    &__icon {
        background-color: $color_bg2;
        
        width: calc(33.3% - 8px);
        height: 300px;
        float: left;
    }
    &__ico-img {
        display: block;
	width: 200px;
	height: 200px;
	background-size: contain;
    margin: 50px auto;
    background-image:url('#{$base_url}/design/ico_sub.png')
    }

    &__text {
        background-color: #fff;
        height: 300px;
        width: calc(66.7% + 8px);
        float: left;
        display: block;
        padding: 60px 5%;
    }

    &__desc {
        font-size: 20px;
        font-family: 'GmarketSansLight', sans-serif;
    }
    &__input {
        height: 60px;
	border: 1px solid #666;
	padding: 0 20px;
	width: calc(100% - 180px);
	max-width: 300px;
	line-height: 60px;
	margin: 0;
	display: block;
	float: left;
	
    }
    &__input::placeholder {
        color: #aaa;
    }
    
    &__submit {
        border: 0;
	width: 180px;
	height: 60px;
	margin: 0;
	color: #fff;
	padding: 16px;
	background-color: #000;
    }
}

.section--footer {
    background: #333;
    position: relative;
    padding: 40px 0;
    color: #eee;
    font-family: 'GmarketSanslight', sans-serif;
    font-size: 17px;
    overflow: hidden;

    &__container {
        @include container_w(1170px);
        padding: 0 15px;
        overflow: hidden;
    }

    &__logo {
        @include indent;
        width: 160px;
        height: 60px;
        display: block;
        float: left;
        background-image: url('#{$base_url}/design/logo_bar_w.svg');
        background-repeat: no-repeat;

    }

